import { useState, useEffect } from "react";
import axios from "axios";

function MainPage() {
  const [companyCode, setCompanyCode] = useState();
  const [companyData, setCompanyData] = useState();

//   17063451
console.log(companyData)

function SearchWithCode(){
    let baseURL = 'http://localhost:3000/itemSelect/{companyCode}'
    useEffect(() => {
        axios.get(baseURL).then((response) => {
            setCompanyData(response.data);
        });
      }, []);


}
  return (
    <div>
      <p className="h3 m-4">ჩანაცვლების პროცედურა</p>

      <nav className="navbar navbar-light bg-light" style={{ width: "100%" }}>
        <form
          className="form-inline d-flex flex-row m-4"
          style={{ width: "60%" }}
        >
          <input
            className="form-control mr-sm-2 mx-2"
            style={{ width: "40%" }}
            type="search"
            placeholder="შეიყვანეთ საწარმოს კოდი"
            aria-label="Search"
            onChange={(e)=> setCompanyCode(e.target.value)}
          />
          <button
            className="btn btn-outline-success my-2 my-sm-0"
            type="submit"
            onClick={SearchWithCode()}
          >
            ძებნა
          </button>
        </form>

        <form
          className="form-inline d-flex flex-row m-4"
          style={{ width: "60%" }}
        >
          <div className="input-group" style={{ width: "25%" }}>
            <div className="input-group-prepend">
              <label
                className="input-group-text mx-2"
                style={{ border: "none", backgroundColor: "white" }}
                htmlFor="inputGroupSelect01"
              >
                Status_results
              </label>
            </div>

            <select className="custom-select" id="inputGroupSelect01 ">
              <option value="1">1</option>
              <option value="2">2</option>
              <option value="3">3</option>
              <option value="4">4</option>
            </select>
          </div>
          <button type="button" className="btn btn-success">
            შერჩევა
          </button>
        </form>
      </nav>
    </div>
  );
}
export default MainPage;
