                  oldCompanyData={companyData[0]}
                  SetcompaniesSuccsess={SetcompaniesSuccsess}
                  SetrejectPopUpRender={SetrejectPopUpRender}
                />
              ) }

              {companiesSuccsess && successItems}
            </tbody>
